    const showAlbums = () => {
        return albums.map( album => {

            console.log(album)

            var key
            album.id
            ? key = album.id
            : key = album.name

            return (
                <Album 
                    key={key}
                    {...album}
                />
            )
        })
    }

    return (
        <div className="album-container">
            {showAlbums()}
        </div>
    )
}
