        this.setState({name: ''})
    }

    handleChange = event => {
        const { name, value } = event.target
        this.setState({ [name]: value })
    }

    render() {
        return (
            <form onSubmit={this.searchArtist} className="artist-search">
                <input
                    type="text"
                    name="artist"
                    value={this.state.artist}
                    onChange={this.handleChange}
                />
                <input
                    type="submit"
                    value="Search Artist"
                />
            </form>
        );
    }
}

export default AddAlbum;
