import AlbumContainer from './AlbumContainer'

import '../App.css';
import FavoritesContainer from './FavoritesContainer';
import AddAlbum from '../components/AddAlbum';
import ToggleFavorites from '../components/ToggleFavorites';

export default function Main({ albums, searchArtist, showFavorites, toggle, favorites }) {

    const artistSearch = () => {
        return (
            <>
                <AddAlbum searchArtist={searchArtist}/>
                <AlbumContainer albums={albums}/>
            </>
        )
    }

    return (
        <main>
            <ToggleFavorites />
            { 
                showFavorites
                ? <FavoritesContainer favorites={favorites}/>
                : artistSearch()
            }
        </main>
    )
}
